var buttons=Array.from(document.querySelectorAll(".modal-button")),modal=document.querySelector(".modal"),emptyField=document.querySelector(".modal__base");buttons.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),modal.classList.add("modal__show")})}),emptyField.addEventListener("click",e=>{e.target===emptyField&&modal.classList.remove("modal__show")}),window.addEventListener("keydown",e=>{27===e.keyCode&&(e.preventDefault(),modal.classList.contains("modal__show")&&modal.classList.toggle("modal__show"))});